INSERT INTO `tblposts` (`id`, `PostTitle`, `CategoryId`, `SubCategoryId`, `PostDetails`, `PostingDate`, `UpdationDate`, `Is_Active`, `PostUrl`, `PostImage`, `viewCounter`, `postedBy`, `lastUpdatedBy`) VALUES
(24, 'BTR Vyn Is Real, Eks Kapten RRQ Hijrah ke Bigetron Alpha untuk MPL ID S12', 1, 3, '<p><font face=\"Comic Sans MS\"><span style=\"font-size: 16px;\">Hanya dalam tempo 24 jam setelah&nbsp;</span><a href=\"https://www.ligagame.tv/mlbb/rrq-resmi-lepas-calvin-vyn-sang-kapten-bakal-gabung-ke-mana\" target=\"_blank\" rel=\"noopener\" style=\"box-sizing: inherit; color: rgb(245, 82, 0); cursor: pointer; font-size: 16px; background-color: rgb(255, 255, 255);\">berpisah dari RRQ</a><span style=\"font-size: 16px;\">,&nbsp;Calvin \"Vyn\" resmi bergabung ke Bigetron Alpa. Pengumuman ini pun telah diunggah dalam bentuk video dan post melalui akun sosial resmi Bigetron Esports. Ini bisa jadi pergerakan yang menarik di bursa transfer jelang bergulirnya Mobile Legends Professional League Indonesia (MPL&nbsp;ID) Season 12.</span></font></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><font face=\"Comic Sans MS\">Vyn yang kini berusia 22 tahun telah menjadi salah satu roamer papan atas di scene MLBB. Bersama RRQ (Hoshi), Vyn telah meraih gelar juara MPL. Kepindahan Vyn ke Bigetron Alpha tentu menyimpan beberapa fakta menarik. Apa itu? Simak ulasannya berikut ini.&nbsp;&nbsp;</font></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><span style=\"box-sizing: inherit; background-color: inherit; color: inherit; font-size: 1rem; caret-color: auto;\"><font face=\"Comic Sans MS\">Pertama, rumor soal Vyn sebenarnya sudah sayup-sayup terdengar di lapangan hingga meja kopi para fans hingga bincang-bincang para KOL saat live stream di Youtube maupun Discord. Saat itu memang mulai berhembus kabar Vyn bakal cabut dari RRQ. Namun ia masih akan tetap berada di scene kompetitif. Nah, soal tim mana yang akan jadi pelabuhannya, nama Bigetron paling santer terdengar.&nbsp;</font></span></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><font face=\"Comic Sans MS\"><span style=\"box-sizing: inherit; background-color: inherit; color: inherit; font-size: 1rem; caret-color: auto;\">Well, maklum, Bigetron Esports bukanlah tim baru bagi Vyn. Yup, BTR Vyn pernah bersama pasukan \"robot merah\" di MPL ID S3 pada tahun 2019. Ia juga sempat bermain untuk tim BOOM jr dan Chronos. Setelah bersama RRQ selama lebih dari empat tahun, Vyn memilih pulang ke pangkuan organisasi</span>&nbsp;BTR.&nbsp;</font></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><font face=\"Comic Sans MS\">Kedua, Vyn mengungkap keputusannya meninggalkan RRQ semata demi mencari tantangan baru.&nbsp;</font></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><font face=\"Comic Sans MS\"><span style=\"box-sizing: inherit; background-color: inherit; color: inherit; font-size: 1rem; caret-color: auto;\"></span></font></p><p style=\"box-sizing: inherit; margin-top: 20px; margin-bottom: 20px; font-size: 16px;\"><font face=\"Comic Sans MS\">Alasan gua keluar (dari RRQ) karena gua merasa sudah ‘stuck’ saja sih. Gua sudah berada pada level yang sama berulang kali dan butuh suasana baru. Ini sudah bicarakan bersama manajemen RRQ dari segi sudut pandang manajemen, mereka mendukung juga.\"</font></p><p><br></p>', '2023-06-11 12:57:17', '2023-06-11 12:58:44', 1, 'BTR-Vyn-Is-Real,-Eks-Kapten-RRQ-Hijrah-ke-Bigetron-Alpha-untuk-MPL-ID-S12', '2602335089fdf3daf4e540831731ffbb.jpg', 1, 'admin', NULL);

